import React, {Component} from 'react';
import { withRouter} from 'react-router-dom'
import { Button } from 'reactstrap';

class UserName extends Component {

    constructor(props) {
        super(props);
        this.state = {
            curName: "Default Username",
            showError: false
        }
        this.startChat = this.startChat.bind(this)
    }

    startChat() {
      let curNameLen = this.state.curName.length 
      if (curNameLen > 0) {
        this.props.setName(this.curName)
        this.props.history.push(`/chat`); 
      } else {
        this.setState({showError: true});
      }
    }

    render() {
      return (
        <div className="App">
          <header className="App-header">
          <h1>
            Testing Web Sockets!
          </h1>
          <h2>
            Chatting as: {this.state.curName} 
          </h2>

            <form onSubmit={this.handleSubmit}>
              <label>
                <p>Set Username to chat</p>
                <textarea value={this.state.curName} onChange={(event) => {
                  this.setState({curName: event.target.value});
                }}/>
              </label>
            </form>

            <button color={{ color: '#092358' }} onClick={this.startChat}>Start Chatting!</button>
            {/* <Button title="Solid Button">ggggggggg</Button> */}

            <div style={{display: this.state.showError ? 'block' : 'none' }}>
              <p>
                Your Username must be longer than 1 character to chat
              </p>
            </div>

          </header>
        </div>
      )
    }
  }

  export default withRouter(UserName);
