import './App.css';
import React, {Component} from 'react';
import SockJsClient from 'react-stomp';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
// import TextField from '@material-ui/core/TextField';

class App extends Component {

  constructor(props) {
      super(props);
      this.state = {
          messages: [],
          typedMessage: "",
          name: "Garrett"
      }
  }

  setName = (name) => {
      console.log(name);
      this.setState({name: name});
  };

  sendMessage = () => {
    this.clientRef.sendMessage('/app/messages', JSON.stringify({
        from: this.state.name,
        text: this.state.typedMessage
    }));
  };

  // displayMessages = () => {
  //   return (
  //     <div>
  //         {this.state.messages.map(msg => {
  //           return (
  //               <div>
  //                   {this.state.name == msg.name ?
  //                     <div>
  //                         <p className="title1">{msg.name} : </p><br/>
  //                         <p>{msg.message}</p>
  //                     </div> :
  //                     <div>
  //                       <p className="title2">{msg.name} : </p><br/>
  //                       <p>{msg.message}</p>
  //                     </div>
  //                   }
  //             </div>)
  //         })}
  //     </div>
  //   );
  // };  

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <a>
            {this.state.name} Testing Web Sockets!
          </a>

          <div className="align-center">
              User : <p className="title1"> {this.state.name}</p>
          </div>
          <div className="align-center">
            <br/><br/>
            <table>
                <tr>
                    {/* <td>
                        <TextField id="outlined-basic" label="Enter Message to Send" variant="outlined"
                          onChange={(event) => {
                              this.setState({typedMessage: event.target.value});
                          }}/>
                    </td> */}
                    {/* <td>
                        <Button variant="contained" color="primary" onClick={this.sendMessage}>Send</Button>
                    </td> */}
                </tr>
              </table>
              <div>
                <Button type="submit">Send</Button>
              </div>
          </div>
          <br/><br/>
          {/* <div className="align-center">
              {this.displayMessages()}
          </div> */}

          <div>
            <SockJsClient url='http://localhost:8080/chat/'
                topics={['/topic/messages']}
                onConnect={() => {
                    console.log("connected");
                }}
                onDisconnect={() => {
                    console.log("Disconnected");
                }}
                onMessage={(msg) => {
                    console.log(msg);
                }}
                ref={(client) => {
                    this.clientRef = client
            }}/>
          </div>
        </header>
      </div>
    )
  }
}

export default App;
