import React, {Component} from 'react';
import { withRouter} from 'react-router-dom'

class UserName extends Component {

    constructor(props) {
        super(props);
        this.state = {
            curName: "Default Username",
            tooShortMessageVisible: false
        }
        this.startChat = this.startChat.bind(this)
    }

    startChat() {
      let curNameLen = this.state.curName.length 
      if (curNameLen > 0) {
        this.props.setName(this.curName)
        this.props.history.push(`/chat`); 
      } else {
        this.setState({tooShortMessageVisible: true});
      }
    }

    render() {
      return (
        <div className="App">
          <header className="App-header">
            <h1>
              {this.state.curName} Testing Web Sockets!
            </h1>

            <form onSubmit={this.handleSubmit}>
              <label>
                <p>User : </p>
                <textarea value={this.state.curName} onChange={(event) => {
                  this.setState({curName: event.target.value});
                }}/>
              </label>
            </form>

            <button onClick={this.startChat}>Start Chatting!</button>

            <div visible={this.state.tooShortMessageVisible}>
              fhfgdf
            </div>

          </header>
        </div>
      )
    }
  }

  export default withRouter(UserName);
