// import './App.css';
import React, {Component} from 'react';
import SockJsClient from 'react-stomp';
import { withRouter} from 'react-router-dom'

class Chat extends Component {

  constructor(props) {
      super(props);
      this.state = {
          messages: [],
          typedMessage: "Message To Send",
          name: this.props.name
      }
  }

  sendMessage = () => {
    this.clientRef.sendMessage('/app/chat', JSON.stringify({
        from: this.state.name,
        text: this.state.typedMessage
    }));
    this.setState({typedMessage: "Message To Send"});
  };

  displayMessages = () => {
    return (
      <div>
          {this.state.messages.map(msg => {
            return (
                <div>
                    {this.state.name === msg.from ?
                      <div>
                        <p style={{ color: '#092358' }}>{msg.from} : {msg.text} : {msg.time}</p>
                      </div> :
                      <div>
                        <p style={{ color: '#c48943' }}>{msg.from} : {msg.text} : {msg.time}</p>
                      </div>
                    }
              </div>)
          })}
      </div>
    );
  };  

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1>
            Testing Web Sockets!
          </h1>
          <h2>
            Chatting as: {this.state.name} 
          </h2>

          <div class="container">
            <div class="row">

              <div class="col-xs-6">
                {/* Submit message area */}
                <form onSubmit={this.handleSubmit}>
                  <label>
                    <p>Message : </p>
                    <textarea className="textarea" value={this.state.typedMessage} onChange={(event) => {
                      this.setState({typedMessage: event.target.value});
                    }}/>
                  </label>
                </form>

                <button className="button" onClick={this.sendMessage}>Send</button>

              </div>

              <div class="col-xs-6"></div>
                {/* Display area */}
                <h6>Messages:</h6>
                <div className="DivWithScroll">
                  <div className="DivToScroll">
                    {this.displayMessages()}
                  </div>
                </div>

            </div>
          </div>

          <div>
            <SockJsClient url='http://localhost:8080/chat/'
              topics={['/topic/messages']}
              onConnect={() => {
                  console.log("connected!");
              }}
              onDisconnect={() => {
                  console.log("Disconnected");
              }}
              onMessage={(msg) => {
                  var jobs = this.state.messages;
                  jobs.push(msg);
                  this.setState({messages: jobs});
                  console.log(msg);
              }}
              ref={(client) => {
                  this.clientRef = client
              }}/>
          </div>
        </header>
      </div>
    )
  }
}

export default withRouter(Chat);
